list-view-config.query-select:
    PREFIX foaf: <http://xmlns.com/foaf/0.1/>
    PREFIX bib: <http://bibliotek-o.org/ontology/>
    PREFIX prov: <http://www.w3.org/ns/prov#>
    PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
    PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
	PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
	PREFIX bibframe: <http://id.loc.gov/ontologies/bibframe/>

    SELECT DISTINCT 
        ?activity ?activityLabel
        ?subclassLabel
        ?agent ?agentLabel
        ?place ?placeLabel
        ?date

    WHERE {
        ?subject ?property ?activity .
        ?activity a bib:Activity .
        OPTIONAL {?activity rdfs:label ?activityLabel .}
        OPTIONAL {
        ?activity bib:hasAgent ?agent .
        ?agent rdfs:label ?agentLabel .
        ?agent rdf:type foaf:Agent .
        }
        OPTIONAL {
        ?activity bf:place ?place .
        ?place rdfs:label ?placeLabel .
        ?agent rdf:type gn:Feature .
        }
        OPTIONAL {
        ?activity bf:date ?date .
        }
        OPTIONAL {
            ?activity vitro:mostSpecificType ?subclass .
            ?subclass rdfs:subClassOf bib:Activity ;
                rdfs:label ?subclassLabel .
        }
    }
