list-view-config.query-construct:
    PREFIX activity: <https://w3id.org/core/activity/0.1/>
    PREFIX arm: <https://w3id.org/core/ontology/0.1/>
    PREFIX ch: <https://w3id.org/custodial_history/ontology/0.1/>
    PREFIX bf: <http://id.loc.gov/ontologies/bibframe/>
    PREFIX ch: <https://w3id.org/core/custodial_history/ontology/0.1/>
    PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
    PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
    PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>        

    CONSTRUCT { 
        ?subject ?property ?objectVar .

        ?objectVar bf:hasPart ?custodialEvent .

        ?custodialEvent a ch:CustodialEvent ;
            vitro:mostSpecificType ?eventType ;                
            rdfs:label ?eventName ;
            arm:atLocation ?eventLocation ;
            bf:date ?eventDate .

        ?eventLocation rdfs:label ?eventLocationName .

        ?activity bf:agent ?agent ;
            arm:atLocation ?activityLocation ;
            bf:date ?activityDate .

        ?agent rdfs:label ?agentName .

        ?activityLocation rdfs:label ?activityLocationName .
     }

     WHERE { {
         ?subject ?property ?objectVar .
         ?objectVar bf:hasPart ?custodialEvent .
         ?custodialEvent a ch:CustodialEvent ;
             vitro:mostSpecificType ?eventType .         

     } UNION {
         ?subject ?property ?objectVar .
         ?objectVar bf:hasPart ?custodialEvent .
         ?custodialEvent a ch:CustodialEvent ;
             vitro:mostSpecificType ?eventType ;
             rdfs:label ?eventName .

     } UNION {
         ?subject ?property ?objectVar .
         ?objectVar bf:hasPart ?custodialEvent .
         ?custodialEvent a ch:CustodialEvent ;
             vitro:mostSpecificType ?eventType ; 
             arm:atLocation ?eventLocation .

     } UNION {
         ?subject ?property ?objectVar .
         ?objectVar bf:hasPart ?custodialEvent .
         ?custodialEvent a ch:CustodialEvent ;
             vitro:mostSpecificType ?eventType ; 
             arm:atLocation ?eventLocation .  
         ?eventLocation rdfs:label ?eventLocationName .

    } UNION {
        ?subject ?property ?objectVar .
        ?objectVar bf:hasPart ?custodialEvent .
        ?custodialEvent a ch:CustodialEvent ;
            vitro:mostSpecificType ?eventType ;
            bf:date ?eventDate .
    } 
}

list-view-config.query-select:
PREFIX activity: <https://w3id.org/core/activity/0.1/>
PREFIX ch: <https://w3id.org/core/ontology/0.1/>
PREFIX bf: <http://id.loc.gov/ontologies/bibframe/>
PREFIX ch: <https://w3id.org/core/custodial_history/ontology/0.1/>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>

SELECT DISTINCT 
    ?custodialEvent
    ?eventType
    ?eventName
    ?eventLocation ?eventLocationName
    ?eventDate

WHERE {        
    ?subject ?property ?objectVar .
    ?objectVar bf:hasPart ?custodialEvent .
    ?custodialEvent a ch:CustodialEvent ;
        vitro:mostSpecificType ?eventType .

    OPTIONAL {                
        ?custodialEvent rdfs:label ?eventName .

    } OPTIONAL {
        ?custodialEvent arm:atLocation ?eventLocation .
        OPTIONAL {               
            ?location rdfs:label ?eventLocationName .  
        }   

    } OPTIONAL {
        ?custodialEvent bf:date ?eventDate .
    }
}
