list-view-config.query-select:
PREFIX foaf: <http://xmlns.com/foaf/0.1/> PREFIX bib: <http://bibliotek-o.org/ontology/> PREFIX prov: <http://www.w3.org/ns/prov#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> PREFIX bibframe: <http://id.loc.gov/ontologies/bibframe/> PREFIX dcterms: <http://purl.org/dc/terms/> PREFIX schema: <http://schema.org/>
SELECT DISTINCT ?instance ?instanceTitle ?publicationAgentName ?publicationDate ?mostSpecificType ?mostSpecificTypeLabel ?publicationLocationName ?identifierValue ?identifierType
WHERE { ?subject ?property ?instance . ?instance a bibframe:Instance . ?instance vitro:mostSpecificType ?mostSpecificType . ?mostSpecificType rdfs:label ?mostSpecificTypeLabel .
OPTIONAL { ?instance bibframe:title ?titleIndividual . ?titleIndividual rdf:type bibframe:Title . ?titleIndividual rdf:value ?instanceTitle . } OPTIONAL { ?instance bib:hasActivity ?publicationActivity . ?publicationActivity a bib:PublicationActivity . OPTIONAL { ?publicationActivity dcterms:date ?publicationDate . } OPTIONAL { ?publicationAgent a foaf:Agent. ?publicationAgent rdfs:label ?publicationAgentName . ?publicationAgent bib:isAgentOf ?publicationActivity . } OPTIONAL { ?publicationActivity schema:location ?publicationLocation . ?publicationLocation rdfs:label ?publicationLocationName . } }
OPTIONAL { ?instance bibframe:identifiedBy ?identifierUri . ?identifierUri rdf:value ?identifierValue . ?identifierUri vitro:mostSpecificType ?identifierType . ?identifierUri rdf:type bibframe:AudioIssueNumber . } }
